import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Header from "./Header";
import Footer from "./Footer";
import Devices from "./Devices";
import AddDevice from "./AddDevice";
import DeleteDevice from "./DeleteDevice";
import Auth from "./Auth";
import HealthCheck from "./HealthCheck";
import GetDevicesCount from "./GetDevicesCount";

function App() {
  const isAuthenticated = !!localStorage.getItem("token"); // Check if user is authenticated
  const email = localStorage.getItem("email"); // Get user email after login

  return (
    <Router>
      <div>
        {isAuthenticated && <Header email={email} />}
        <main style={{ padding: "20px" }}>
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<Auth />} />

            {/* Private Routes */}
            {isAuthenticated && (
              <>
                <Route path="/landing" element={<Landing email={email} />} />
                <Route path="/devices" element={<Devices />} />
                <Route path="/adddevice" element={<AddDevice />} />
                <Route path="/deletedevice" element={<DeleteDevice />} />
              </>
            )}

            {/* Redirect to login if not authenticated */}
            {!isAuthenticated && <Route path="*" element={<Auth />} />}
          </Routes>
        </main>
        {isAuthenticated && <Footer />}
      </div>
    </Router>
  );
}

const Landing = ({ email }) => {
  return (
    <div>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <h1 style={{ textAlign: "center" }}>Welcome to React Assignment</h1>
        <div style={{ display: "flex", alignItems: "center" }}>
          <p style={{ marginRight: "20px" }}>Welcome, {email.split('@')[0]}</p>
          <HealthCheck />
          <NotificationCount />
          <LogoutButton />
        </div>
      </div>
      <div style={{ display: "flex", justifyContent: "space-between", marginTop: "20px" }}>
        <div style={{ flex: 1 }}>
          <DevicesList />
        </div>
        <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
          <button onClick={() => (window.location.href = "/adddevice")}>Add Device</button>
          <button onClick={() => (window.location.href = "/deletedevice")}>Delete Device</button>
        </div>
        <div style={{ flex: 1 }}>
          <GetDevicesCount />
        </div>
      </div>
    </div>
  );
};

const DevicesList = () => {
  const [devices, setDevices] = React.useState([]);

  React.useEffect(() => {
    fetch("/api/devices")
      .then((res) => res.json())
      .then((data) => setDevices(data))
      .catch(console.error);
  }, []);

  return (
    <div>
      <h2>Devices</h2>
      <ul>
        {devices.length > 0 ? (
          devices.map((device, index) => <li key={index}>{device.name}</li>)
        ) : (
          <p>No devices available</p>
        )}
      </ul>
    </div>
  );
};

const NotificationCount = () => {
  const [deviceCount, setDeviceCount] = React.useState(0);

  React.useEffect(() => {
    fetch("/api/devices/count")
      .then((res) => res.json())
      .then((data) => setDeviceCount(data.count))
      .catch(console.error);
  }, []);

  return (
    <div style={{ marginRight: "20px", cursor: "pointer" }} title={`Total Devices: ${deviceCount}`}>
      <span>🔔</span>
    </div>
  );
};

const LogoutButton = () => {
  const navigate = useNavigate();

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/");
  };

  return (
    <button
      onClick={logout}
      style={{
        padding: "10px",
        background: "#f00",
        color: "#fff",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
      }}
    >
      Logout
    </button>
  );
};

export default App;
