import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import axios from 'axios'; // Import axios to make API requests

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();  // Hook for redirection

  const handleLogin = async (e) => {
    e.preventDefault();

    // Validate input
    if (!email || !password) {
      setErrorMessage('Email and password are required');
      return;
    }

    try {
      // Send login request to the backend
      const response = await axios.post('http://localhost:5000/api/login', { email, password });
      
      // On success, store token and redirect to chart.html
      if (response.status === 200) {
        localStorage.setItem('token', response.data.token); // Store token in localStorage
        navigate('/chart.html'); // Redirect to chart.html
      }
    } catch (error) {
      setErrorMessage(error.response?.data?.message || 'Login failed'); // Display error message
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            placeholder="Enter your email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
          />
        </div>
        <div>
          <label>Password:</label>
          <input 
            type="password" 
            placeholder="Enter your password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
          />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
      {errorMessage && <p>{errorMessage}</p>} {/* Display error message */}
    </div>
  );
};

export default Login;
