import React, { useState } from 'react';
import axios from 'axios';
import './AddDevice.css';

const AddDevice = () => {
  const [deviceId, setDeviceId] = useState('');
  const [createdBy, setCreatedBy] = useState('');
  const [response, setResponse] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleAddDevice = async (e) => {
    e.preventDefault();

    if (!deviceId.trim() || !createdBy.trim()) {
      setResponse('Device ID and Created By fields cannot be empty.');
      return;
    }

    setIsLoading(true); // Show loader or disable button
    try {
      const result = await axios.post('http://localhost:5000/api/devices', {
        device_id: deviceId,
        created_by: createdBy,
      });
      setResponse(result.data.message || 'Device Added Successfully!');
      setDeviceId(''); // Clear input field
      setCreatedBy(''); // Clear input field
    } catch (error) {
      setResponse('Failed to Add Device! Please try again.');
    } finally {
      setIsLoading(false); // Hide loader or enable button
    }
  };

  return (
    <div className="add-device-container">
      <h2>Add Device</h2>
      <form onSubmit={handleAddDevice}>
        <div className="form-group">
          <label htmlFor="deviceId">Device ID:</label>
          <input
            id="deviceId"
            type="text"
            placeholder="Enter Unique Device ID"
            value={deviceId}
            onChange={(e) => setDeviceId(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="createdBy">Created By:</label>
          <input
            id="createdBy"
            type="text"
            placeholder="Enter Your Name or ID"
            value={createdBy}
            onChange={(e) => setCreatedBy(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Adding...' : 'Add Device'}
        </button>
      </form>
      {response && <p className="response-message">{response}</p>}
    </div>
  );
};

export default AddDevice;
