import React, { useState } from 'react';
import axios from 'axios';

const DeleteDevice = () => {
  const [deviceId, setDeviceId] = useState('');
  const [response, setResponse] = useState('');

  const handleDeleteDevice = async () => {
    try {
      const result = await axios.delete(`http://localhost:5000/api/devices/${deviceId}`);
      setResponse(result.data.message || 'Device Deleted Successfully!');
    } catch (error) {
      setResponse('Failed to Delete Device!');
    }
  };

  return (
    <div>
      <h2>Delete Device</h2>
      <input
        type="text"
        placeholder="Enter Device ID"
        value={deviceId}
        onChange={(e) => setDeviceId(e.target.value)}
      />
      <button onClick={handleDeleteDevice}>Delete Device</button>
      {response && <p>{response}</p>}
    </div>
  );
};

export default DeleteDevice;
