import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Auth.css";
import { Email, Lock, Login, PersonAdd } from "@mui/icons-material";

const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState("");
  const [messageColor, setMessageColor] = useState("blue"); // State to track message color
  const navigate = useNavigate();

  const emailRegex = /^[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.(com|in)$/;

  const handleAction = async (action) => {
    setMessage("");
    setMessageColor("blue"); // Default message color

    if (!email || !password) {
      setMessage("Email and password are required");
      setMessageColor("red");
      return;
    }

    if (!emailRegex.test(email)) {
      setMessage("Please enter a valid email address");
      setMessageColor("red");
      return;
    }

    try {
      if (action === "login") {
        const response = await axios.post("http://localhost:5000/api/login", {
          email,
          password,
        });
        if (response.status === 200) {
          const username = email.split("@")[0];
          localStorage.setItem("token", response.data.token);
          localStorage.setItem("username", username);
          navigate("/landing");
        }
      } else if (action === "signup") {
        const response = await axios.post("http://localhost:5000/api/signup", {
          email,
          password,
        });
        if (response.status === 201) {
          setMessage("Signup successful! Please login.");
          setMessageColor("green"); // Green for success
        }
      }
    } catch (error) {
      setMessage(
        error.response?.data?.message || "An error occurred. Please try again."
      );
      setMessageColor("red"); // Red for errors
    }
  };

  return (
    <div className="auth-container">
      <h2>Authentication</h2>
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="input-group">
          <Email className="icon" />
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
        </div>
        <div className="input-group">
          <Lock className="icon" />
          <input
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />
        </div>
        <div className="checkbox-group">
          <label>
            <input
              type="checkbox"
              checked={showPassword}
              onChange={() => setShowPassword(!showPassword)}
            />
            Show Password
          </label>
        </div>
        <div className="button-group">
          <button
            type="button"
            className="action-button"
            onClick={() => handleAction("login")}
          >
            <Login className="button-icon" /> Login
          </button>
          <button
            type="button"
            className="action-button"
            onClick={() => handleAction("signup")}
          >
            <PersonAdd className="button-icon" /> Signup
          </button>
        </div>
      </form>
      {message && (
        <p className="message" style={{ color: messageColor }}>
          {message}
        </p>
      )}
    </div>
  );
};

export default Auth;
