import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // Regular expression for email validation
  const emailRegex = /^[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.(com|in)$/;

  const handleAction = async (action) => {
    setMessage("");

    if (!email || !password) {
      setMessage("Email and password are required");
      return;
    }

    if (!emailRegex.test(email)) {
      setMessage("Please enter a valid email address");
      return;
    }

    try {
      if (action === "login") {
        const response = await axios.post("http://localhost:5000/api/login", { email, password });
        if (response.status === 200) {
          localStorage.setItem("token", response.data.token);
          navigate("/devices"); // Redirect to the devices page
        }
      } else if (action === "signup") {
        const response = await axios.post("http://localhost:5000/api/signup", { email, password });
        if (response.status === 201) {
          setMessage("Signup successful! Please login.");
        }
      }
    } catch (error) {
      setMessage(error.response?.data?.message || "An error occurred. Please try again.");
    }
  };

  return (
    <div className="auth-container">
      <h2>Authentication</h2>
      <form onSubmit={(e) => e.preventDefault()}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />
        </div>
        <div className="button-group">
          <button type="button" onClick={() => handleAction("login")}>Login</button>
          <button type="button" onClick={() => handleAction("signup")}>Signup</button>
        </div>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Auth;
