import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart, PieController, ArcElement, Tooltip, Legend } from "chart.js";
import AddDevice from "./AddDevice"; // Import AddDevice component

Chart.register(PieController, ArcElement, Tooltip, Legend);

const Landing = () => {
  const [devices, setDevices] = useState([]);
  const [deviceMessage, setDeviceMessage] = useState("Fetching devices...");
  const [chartData, setChartData] = useState(null);
  const [chartInstance, setChartInstance] = useState(null);
  const [isAddDeviceModalOpen, setIsAddDeviceModalOpen] = useState(false); // Add state for Add Device modal
  const navigate = useNavigate();
  const username = localStorage.getItem("username");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/");
  };

  useEffect(() => {
    const fetchDevices = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/getalldevices");
        if (response.data && response.data.devices.length > 0) {
          setDevices(response.data.devices);
        } else {
          setDeviceMessage("No devices found.");
        }
      } catch (error) {
        setDeviceMessage("Error fetching devices.");
      }
    };
    fetchDevices();
  }, []);

  const fetchDeviceCounts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/devicescount");
      const data = response.data.counts;
      setChartData(data);

      if (chartInstance) {
        chartInstance.data.datasets[0].data = [
          data.countWithinOneHour,
          data.countBeforeOneHour,
        ];
        chartInstance.update();
      } else {
        initializeChart(data);
      }
    } catch (error) {
      console.error("Error fetching device counts:", error);
    }
  };

  const initializeChart = (data) => {
    const ctx = document.getElementById("deviceChart").getContext("2d");
    const newChartInstance = new Chart(ctx, {
      type: "pie",
      data: {
        labels: ["Created within the last hour", "Created before one hour"],
        datasets: [
          {
            label: "Device Count",
            data: [data.countWithinOneHour, data.countBeforeOneHour],
            backgroundColor: ["#36A2EB", "#FF6384"],
            borderColor: ["#36A2EB", "#FF6384"],
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: { position: "top" },
        },
      },
    });
    setChartInstance(newChartInstance);
  };

  useEffect(() => {
    fetchDeviceCounts();
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Hello, Welcome to API Assignment</h1>
      <div style={styles.header}>
        <p style={styles.greeting}>Hello, {username}!</p>
        <button style={styles.logoutButton} onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div style={styles.columns}>
        {/* First Column */}
        <div style={styles.column}>
          <h2>Devices</h2>
          {devices.length > 0 ? (
            devices.map((device, index) => (
              <div key={index} style={styles.deviceItem}>
                <p>Device ID: {device.device_id}</p>
                <p>Created By: {device.created_by}</p>
                <p>Created Time: {new Date(device.created_time).toLocaleString()}</p>
              </div>
            ))
          ) : (
            <p>{deviceMessage}</p>
          )}
        </div>

        {/* Second Column */}
        <div style={styles.column}>
          <h2>Actions</h2>
          <button
            style={styles.actionButton}
            onClick={() => setIsAddDeviceModalOpen(true)} // Show Add Device modal on button click
          >
            Add Device
          </button>
          <button style={styles.actionButton}>Delete Device</button>
        </div>

        {/* Third Column */}
        <div style={styles.column}>
          <h2>Device Count</h2>
          <div
            style={{
              width: "100%",
              maxWidth: "400px",
              height: "300px",
              margin: "0 auto",
            }}
          >
            <canvas id="deviceChart"></canvas>
          </div>
        </div>
      </div>

      {/* Conditionally render AddDevice modal */}
      {isAddDeviceModalOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modal}>
            <button
              style={styles.closeButton}
              onClick={() => setIsAddDeviceModalOpen(false)} // Close modal
            >
              X
            </button>
            <AddDevice />
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#f0f4f8",
    padding: "20px",
  },
  header: {
    position: "absolute",
    top: "10px",
    right: "20px",
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  greeting: {
    fontSize: "1.2rem",
    color: "#555",
    fontFamily: "'Roboto', sans-serif",
  },
  logoutButton: {
    padding: "5px 10px",
    fontSize: "1rem",
    color: "#fff",
    backgroundColor: "#007bff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  heading: {
    fontSize: "2rem",
    color: "#333",
    fontFamily: "'Roboto', sans-serif",
    textAlign: "center",
    marginBottom: "30px",
  },
  columns: {
    display: "flex",
    justifyContent: "space-around",
    width: "100%",
  },
  column: {
    flex: 1,
    margin: "0 10px",
    textAlign: "center",
  },
  deviceItem: {
    fontSize: "1rem",
    marginBottom: "5px",
  },
  actionButton: {
    padding: "10px",
    margin: "10px",
    fontSize: "1rem",
    color: "#fff",
    backgroundColor: "#007bff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  modal: {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "5px",
    width: "400px",
    position: "relative",
  },
  closeButton: {
    position: "absolute",
    top: "10px",
    right: "10px",
    backgroundColor: "#f44336",
    color: "#fff",
    border: "none",
    borderRadius: "50%",
    padding: "10px",
    cursor: "pointer",
  },
};

export default Landing;
