import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Signup from './components/Signup';
import Dashboard from './pages/Dashboard';
import HealthCheck from './pages/HealthCheck';
import GetAllDevices from './pages/GetAllDevices';
import GetDevicesCount from './pages/GetDevicesCount';
import GetNotificationCount from './pages/GetNotificationCount';
import AddDevice from './pages/AddDevice';
import DeleteDevice from './pages/DeleteDevice';
import './styles.css'; // Import global styles

const App = () => {
  const isAuthenticated = localStorage.getItem('token'); // Check if user is authenticated

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route
          path="/dashboard"
          element={isAuthenticated ? <Dashboard /> : <Navigate to="/" />}
        />
        <Route
          path="/healthcheck"
          element={isAuthenticated ? <HealthCheck /> : <Navigate to="/" />}
        />
        <Route
          path="/getalldevices"
          element={isAuthenticated ? <GetAllDevices /> : <Navigate to="/" />}
        />
        <Route
          path="/getdevicescount"
          element={isAuthenticated ? <GetDevicesCount /> : <Navigate to="/" />}
        />
        <Route
          path="/getnotificationcount"
          element={isAuthenticated ? <GetNotificationCount /> : <Navigate to="/" />}
        />
        <Route
          path="/adddevice"
          element={isAuthenticated ? <AddDevice /> : <Navigate to="/" />}
        />
        <Route
          path="/deletedevice"
          element={isAuthenticated ? <DeleteDevice /> : <Navigate to="/" />}
        />
        {/* Redirect to Login page if no route is matched */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
