import React, { useState } from 'react';
import axios from 'axios';

const GetAllDevices = () => {
  const [devices, setDevices] = useState([]);

  const handleGetAllDevices = async () => {
    try {
      const result = await axios.get('http://localhost:5000/api/devices');
      setDevices(result.data || []);
    } catch (error) {
      setDevices([]);
    }
  };

  return (
    <div>
      <h2>All Devices</h2>
      <button onClick={handleGetAllDevices}>Fetch Devices</button>
      <ul>
        {devices.map((device, index) => (
          <li key={index}>{device.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default GetAllDevices;
