import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Auth = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleAction = async (action) => {
    setMessage(''); // Clear previous messages
    if (!email || !password) {
      setMessage('Email and password are required');
      return;
    }

    try {
      if (action === 'login') {
        // Login logic
        const response = await axios.post('http://localhost:5000/api/login', { email, password });
        if (response.status === 200) {
          localStorage.setItem('token', response.data.token);
          navigate('/dashboard'); // Redirect to dashboard on successful login
        }
      } else if (action === 'signup') {
        // Signup logic
        const response = await axios.post('http://localhost:5000/api/signup', { email, password });
        if (response.status === 201) {
          setMessage('Signup successful! Please try to login.');
        }
      }
    } catch (error) {
      // Handle errors for both login and signup
      if (action === 'login') {
        setMessage(error.response?.data?.message || 'Login failed. Please check your credentials.');
      } else if (action === 'signup') {
        setMessage(error.response?.data?.message || 'User already exists.');
      }
    }
  };

  return (
    <div>
      <h2>Authentication</h2>
      <form onSubmit={(e) => e.preventDefault()}>
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div style={{ marginTop: '1rem' }}>
          <button type="button" onClick={() => handleAction('login')}>
            Login
          </button>
          <button type="button" onClick={() => handleAction('signup')}>
            Signup
          </button>
        </div>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Auth;
