import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Auth = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // Regular expression for email validation (basic check for @ and .com/.in)
  const emailRegex = /^[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.(com|in)$/;

  const handleAction = async (action) => {
    setMessage(''); // Clear previous messages

    // Basic validation for empty fields
    if (!email || !password) {
      setMessage('Email and password are required');
      return;
    }

    // Email validation using regex
    if (!emailRegex.test(email)) {
      setMessage('Please enter a valid email address (e.g., example@domain.com or example@domain.in)');
      return;
    }

    try {
      if (action === 'login') {
        // Login logic
        const response = await axios.post('http://localhost:5000/api/login', { email, password });
        if (response.status === 200) {
          localStorage.setItem('token', response.data.token);
          navigate('/dashboard'); // Redirect to dashboard on successful login
        }
      } else if (action === 'signup') {
        // Signup logic
        const response = await axios.post('http://localhost:5000/api/signup', { email, password });
        if (response.status === 201) {
          setMessage('Signup successful! Please try to login.');
        }
      }
    } catch (error) {
      // Handle errors for both login and signup
      if (action === 'login') {
        setMessage(error.response?.data?.message || 'Login failed. Please check your credentials.');
      } else if (action === 'signup') {
        setMessage(error.response?.data?.message || 'User already exists.');
      }
    }
  };

  return (
    <div className="auth-container">
      <h2>Authentication</h2>
      <form onSubmit={(e) => e.preventDefault()}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />
        </div>
        <div className="button-group">
          <button type="button" onClick={() => handleAction('login')}>Login</button>
          <button type="button" onClick={() => handleAction('signup')}>Signup</button>
        </div>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Auth;
