import React, { useEffect, useState } from "react";
import GetDevicesCount from "./GetDevicesCount";
import "./Devices.css";

function Devices() {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    // Fetch devices from the database
    fetch("/api/devices")
      .then((res) => res.json())
      .then((data) => setDevices(data))
      .catch(console.error);
  }, []);

  return (
    <div className="devices">
      <h2>Devices</h2>
      <ul>
        {devices.length > 0 ? (
          devices.map((device, index) => <li key={index}>{device.name}</li>)
        ) : (
          <p>No devices available</p>
        )}
      </ul>
      <div className="device-actions">
        <button onClick={() => (window.location.href = "/adddevice")}>
          Add Device
        </button>
        <button onClick={() => (window.location.href = "/deletedevice")}>
          Delete Device
        </button>
      </div>
      <div className="device-chart">
        <GetDevicesCount />
      </div>
    </div>
  );
}

export default Devices;
