import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import axios from 'axios'; // Import axios to make API requests

const Login = () => {
  const [email, setEmail] = useState(''); // State for email
  const [password, setPassword] = useState(''); // State for password
  const [errorMessage, setErrorMessage] = useState(''); // State for error message
  const navigate = useNavigate();  // Hook for redirection

  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent the default form submission

    // Validate input
    if (!email || !password) {
      setErrorMessage('Email and password are required');
      return;
    }

    try {
      // Send login request to the backend
      const response = await axios.post('http://localhost:5000/api/login', { email, password });
      
      // On success, store token and redirect to chart.html
      if (response.status === 200) {
        localStorage.setItem('token', response.data.token); // Store token in localStorage
        navigate('/chart.html'); // Redirect to chart.html
      }
    } catch (error) {
      // Handle error and display error message
      setErrorMessage(error.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            placeholder="Enter your email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} // Update email state
          />
        </div>
        <div>
          <label>Password:</label>
          <input 
            type="password" 
            placeholder="Enter your password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} // Update password state
          />
        </div>
        <div>
          <button type="submit">Login</button> {/* Submit login */}
        </div>
      </form>
      {errorMessage && <p>{errorMessage}</p>} {/* Display error message if any */}
      
      {/* Sign Up redirect */}
      <p>
        Don't have an account? 
        <Link to="/signup">
          <button>Sign Up</button>
        </Link> {/* Button to go to signup */}
      </p>
    </div>
  );
};

export default Login;
