import React from "react";

const Landing = () => {
  const username = localStorage.getItem("username"); // Retrieve username from localStorage

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <p style={styles.greeting}>Hello, {username}! Welcome to API Assignment</p>
      </div>
      <h1 style={styles.heading}>Hello HI HOW ARE YOU, Welcome to API Assignment</h1>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#f0f4f8",
    padding: "20px",
  },
  header: {
    position: "absolute",
    top: "10px",
    right: "20px",
  },
  greeting: {
    fontSize: "1.2rem",
    color: "#555",
    fontFamily: "'Roboto', sans-serif",
  },
  heading: {
    fontSize: "2rem",
    color: "#333",
    fontFamily: "'Roboto', sans-serif",
    textAlign: "center",
  },
};

export default Landing;
