import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart, PieController, ArcElement, Tooltip, Legend } from "chart.js";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBell,
  faSignOutAlt,
  faPlus,
  faTrash,
  faChartPie,
  faList,
  faUser,
  faBuilding,
  faEnvelope,
  faPhone,
} from "@fortawesome/free-solid-svg-icons";
import AddDevice from "./AddDevice";
import DeleteDevice from "./DeleteDevice";

Chart.register(PieController, ArcElement, Tooltip, Legend);

const Landing = () => {
  const [devices, setDevices] = useState([]);
  const [deviceMessage, setDeviceMessage] = useState("Fetching devices...");
  const [chartData, setChartData] = useState(null);
  const [chartInstance, setChartInstance] = useState(null);
  const [showAddDevice, setShowAddDevice] = useState(false);
  const [showDeleteDevice, setShowDeleteDevice] = useState(false);
  const [status, setStatus] = useState("Checking...");
  const [tooltip, setTooltip] = useState("");
  const [notificationMessage, setNotificationMessage] = useState("");
  const [isNotificationVisible, setIsNotificationVisible] = useState(false);
  const navigate = useNavigate();
  const username = localStorage.getItem("username");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/");
  };

  useEffect(() => {
    const fetchDevices = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/getalldevices");
        if (response.data && response.data.devices.length > 0) {
          setDevices(response.data.devices);
        } else {
          setDeviceMessage("No devices found.");
        }
      } catch (error) {
        setDeviceMessage("Error fetching devices.");
      }
    };
    fetchDevices();
  }, []);

  const fetchDeviceCounts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/devicescount");
      const data = response.data.counts;
      setChartData(data);

      if (chartInstance) {
        chartInstance.data.datasets[0].data = [
          data.countWithinOneHour,
          data.countBeforeOneHour,
        ];
        chartInstance.update();
      } else {
        initializeChart(data);
      }
    } catch (error) {
      console.error("Error fetching device counts:", error);
    }
  };

  const initializeChart = (data) => {
    const ctx = document.getElementById("deviceChart").getContext("2d");
    const newChartInstance = new Chart(ctx, {
      type: "pie",
      data: {
        labels: ["Created within the last hour", "Created before one hour"],
        datasets: [
          {
            label: "Device Count",
            data: [data.countWithinOneHour, data.countBeforeOneHour],
            backgroundColor: ["#4CAF50", "#FF5722"],
            borderColor: ["#4CAF50", "#FF5722"],
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: { position: "top" },
        },
      },
    });
    setChartInstance(newChartInstance);
  };

  useEffect(() => {
    fetchDeviceCounts();
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Welcome to API Assignment</h1>
      <div style={styles.header}>
        <div style={styles.statusAndLogout}>
          <p
            style={status === "Active" ? styles.activeStatus : styles.deactiveStatus}
            title={tooltip}
          >
            {status}
          </p>
          <button style={styles.logoutButton} onClick={handleLogout}>
            <FontAwesomeIcon icon={faSignOutAlt} /> Logout
          </button>
        </div>
        <div style={styles.notificationContainer}>
          <FontAwesomeIcon
            icon={faBell}
            style={styles.notificationIcon}
            title="Notifications"
          />
        </div>
        <p style={styles.greeting}>
          Hello, <span style={styles.username}>{username}</span>!
        </p>
      </div>
      <div style={styles.columns}>
        <div style={styles.column}>
          <h2>
            <FontAwesomeIcon icon={faList} /> Devices
          </h2>
          <div style={styles.deviceListContainer}>
            {devices.length > 0 ? (
              devices.map((device, index) => (
                <div key={index} style={styles.deviceItem}>
                  <p>Device ID: {device.device_id}</p>
                  <p>Created By: {device.created_by}</p>
                  <p>Created Time: {new Date(device.created_time).toLocaleString()}</p>
                </div>
              ))
            ) : (
              <p>{deviceMessage}</p>
            )}
          </div>
        </div>

        <div style={styles.column}>
          <h2>
            <FontAwesomeIcon icon={faPlus} /> Actions
          </h2>
          <button style={styles.actionButton} onClick={() => setShowAddDevice(true)}>
            <FontAwesomeIcon icon={faPlus} /> Add Device
          </button>
        </div>

        <div style={styles.column}>
          <h2>
            <FontAwesomeIcon icon={faChartPie} /> Device Count
          </h2>
          <div style={{ width: "100%", maxWidth: "400px", height: "300px" }}>
            <canvas id="deviceChart"></canvas>
          </div>
        </div>
      </div>

      <div style={styles.footer}>
        <p>
          <FontAwesomeIcon icon={faUser} /> Sughnan H R
        </p>
        <p>
          <FontAwesomeIcon icon={faBuilding} /> Alten GT
        </p>
        <p>
          <FontAwesomeIcon icon={faEnvelope} /> hrsughnankiran389@gmail.com
        </p>
        <p>
          <FontAwesomeIcon icon={faPhone} /> 9480146265
        </p>
      </div>
    </div>
  );
};
const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "#1a202c",
    padding: "20px",
    color: "#f6e58d",
    fontFamily: "'Roboto', sans-serif",
  },
  heading: {
    fontSize: "2.5rem",
    color: "#f6e58d",
    textAlign: "center",
    marginBottom: "20px",
    fontWeight: "bold",
  },
  header: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%",
    marginBottom: "20px",
  },
  statusAndLogout: {
    display: "flex",
    alignItems: "center",
    gap: "15px",
  },
  greeting: {
    fontSize: "1.2rem",
    color: "#f6e58d",
  },
  notificationContainer: {
    position: "relative",
  },
  notificationIcon: {
    fontSize: "1.5rem",
    color: "#f6e58d",
    cursor: "pointer",
  },
  activeStatus: {
    fontSize: "1rem",
    color: "#38a169",
    fontWeight: "bold",
  },
  deactiveStatus: {
    fontSize: "1rem",
    color: "#e53e3e",
    fontWeight: "bold",
  },
  logoutButton: {
    width: "160px",
    height: "40px",
    fontSize: "1rem",
    color: "#f6e58d",
    backgroundColor: "#ff7f50", 
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontWeight: "bold",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    transition: "transform 0.2s",
  },
  columns: {
    display: "flex",
    justifyContent: "space-around",
    width: "100%",
    marginTop: "20px",
  },
  column: {
    flex: 1,
    margin: "0 15px",
    textAlign: "center",
    color: "#f6e58d",
  },
  deviceListContainer: {
    maxHeight: "300px",
    overflowY: "auto",
    backgroundColor: "#2d3748",
    padding: "10px",
    borderRadius: "8px",
  },
  deviceItem: {
    backgroundColor: "#4a5568",
    color: "#f6e58d",
    padding: "15px",
    margin: "10px 0",
    borderRadius: "8px",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
  },
  actionButton: {
    width: "160px",
    height: "40px",
    fontSize: "1rem",
    color: "#f6e58d",
    backgroundColor: "#ff7f50", 
    border: "none",
    borderRadius: "8px",
