import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import HealthCheck from "./HealthCheck";
import "./Header.css";

function Header() {
  const navigate = useNavigate();
  const [userEmail, setUserEmail] = useState("");
  const [deviceCount, setDeviceCount] = useState(0);

  useEffect(() => {
    // Mock user email from authentication
    setUserEmail("user@example.com");

    // Fetch device count from API
    fetch("/api/devicecount")
      .then((res) => res.json())
      .then((data) => setDeviceCount(data.count))
      .catch(console.error);
  }, []);

  const logout = () => {
    // Perform logout and redirect
    navigate("/login");
  };

  return (
    <header className="header">
      <div className="header-greeting">Welcome, {userEmail.split("@")[0]}</div>
      <div className="header-health">
        <HealthCheck />
      </div>
      <div className="header-notification" onClick={() => alert(`Devices: ${deviceCount}`)}>
        üõéÔ∏è
      </div>
      <button className="header-logout" onClick={logout}>
        Logout
      </button>
    </header>
  );
}

export default Header;
