import React, { useState } from 'react';
import axios from 'axios';
import './DeleteDevice.css';

const DeleteDevice = () => {
  const [deviceId, setDeviceId] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleDeleteDevice = async () => {
    if (!deviceId.trim()) {
      setResponse('Please enter a device ID.');
      return;
    }

    setLoading(true);
    setResponse('');
    try {
      const result = await axios.delete(`http://localhost:5000/api/devices/${deviceId}`);
      setResponse(result.data.message || 'Device Deleted Successfully!');
    } catch (error) {
      setResponse('Failed to Delete Device!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="delete-device-container">
      <h2>Delete Device</h2>
      <input
        type="text"
        placeholder="Enter Device ID"
        value={deviceId}
        onChange={(e) => setDeviceId(e.target.value)}
      />
      <button onClick={handleDeleteDevice} disabled={loading}>
        {loading ? 'Deleting...' : 'Delete Device'}
      </button>
      {response && <p>{response}</p>}
    </div>
  );
};

export default DeleteDevice;
