import React, { useState } from 'react';
import axios from 'axios';

const DeviceForm = ({ deviceId }) => {
  const [name, setName] = useState('');
  const [model, setModel] = useState('');
  const token = localStorage.getItem('token');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const deviceData = { name, model };
    const headers = { Authorization: `Bearer ${token}` };

    if (deviceId) {
      try {
        await axios.put(`http://localhost:5000/api/devices/${deviceId}`, deviceData, { headers });
        alert('Device updated successfully');
      } catch (error) {
        console.error('Error updating device:', error);
      }
    } else {
      try {
        await axios.post('http://localhost:5000/api/devices', deviceData, { headers });
        alert('Device added successfully');
      } catch (error) {
        console.error('Error adding device:', error);
      }
    }
  };

  return (
    <div>
      <h2>{deviceId ? 'Edit Device' : 'Add Device'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Model:</label>
          <input
            type="text"
            value={model}
            onChange={(e) => setModel(e.target.value)}
            required
          />
        </div>
        <div>
          <button type="submit">{deviceId ? 'Update Device' : 'Add Device'}</button>
        </div>
      </form>
    </div>
  );
};

export default DeviceForm;
