import React, { useState } from 'react';
import axios from 'axios';

const GetDevicesCount = () => {
  const [count, setCount] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleGetDevicesCount = async () => {
    setLoading(true);
    setError(null); // Reset error state before fetching
    try {
      const result = await axios.get('http://localhost:5000/api/devices/count');
      setCount(result.data.count);
    } catch (error) {
      setError('Error fetching count');
      setCount(null); // Reset count in case of an error
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Devices Count</h2>
      <button onClick={handleGetDevicesCount} disabled={loading}>
        {loading ? 'Fetching Count...' : 'Get Count'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {count !== null && !error && <p>Total Devices: {count}</p>}
    </div>
  );
};

export default GetDevicesCount;
