import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart, PieController, ArcElement, Tooltip, Legend } from "chart.js";
import AddDevice from "./AddDevice"; // Import the AddDevice component
import DeleteDevice from "./DeleteDevice"; // Import the DeleteDevice component

Chart.register(PieController, ArcElement, Tooltip, Legend);

const Landing = () => {
  const [devices, setDevices] = useState([]);
  const [deviceMessage, setDeviceMessage] = useState("Fetching devices...");
  const [chartData, setChartData] = useState(null);
  const [chartInstance, setChartInstance] = useState(null);
  const [showAddDevice, setShowAddDevice] = useState(false); // State to toggle AddDevice form
  const [showDeleteDevice, setShowDeleteDevice] = useState(false); // State to toggle DeleteDevice form
  const [status, setStatus] = useState("Checking..."); // State for health status
  const [tooltip, setTooltip] = useState(""); // State for tooltip message
  const navigate = useNavigate();
  const username = localStorage.getItem("username");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/");
  };

  // Fetch devices
  useEffect(() => {
    const fetchDevices = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/getalldevices");
        if (response.data && response.data.devices.length > 0) {
          setDevices(response.data.devices);
        } else {
          setDeviceMessage("No devices found.");
        }
      } catch (error) {
        setDeviceMessage("Error fetching devices.");
      }
    };
    fetchDevices();
  }, []);

  // Fetch device counts
  const fetchDeviceCounts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/devicescount");
      const data = response.data.counts;
      setChartData(data);

      if (chartInstance) {
        chartInstance.data.datasets[0].data = [
          data.countWithinOneHour,
          data.countBeforeOneHour,
        ];
        chartInstance.update();
      } else {
        initializeChart(data);
      }
    } catch (error) {
      console.error("Error fetching device counts:", error);
    }
  };

  // Initialize chart
  const initializeChart = (data) => {
    const ctx = document.getElementById("deviceChart").getContext("2d");
    const newChartInstance = new Chart(ctx, {
      type: "pie",
      data: {
        labels: ["Created within the last hour", "Created before one hour"],
        datasets: [
          {
            label: "Device Count",
            data: [data.countWithinOneHour, data.countBeforeOneHour],
            backgroundColor: ["#36A2EB", "#FF6384"],
            borderColor: ["#36A2EB", "#FF6384"],
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: { position: "top" },
        },
      },
    });
    setChartInstance(newChartInstance);
  };

  useEffect(() => {
    fetchDeviceCounts();
  }, []);

  // Fetch health status
  useEffect(() => {
    const checkHealth = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/healthcheck");
        if (response.data.statusCode === 200) {
          setStatus("Active");
          setTooltip("API is running and database connection is healthy");
        } else {
          setStatus("Deactive");
          setTooltip("API is down or database connection is unhealthy");
        }
      } catch (error) {
        setStatus("Deactive");
        setTooltip("Failed to connect to the API or database");
      }
    };
    checkHealth();
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Hello, Welcome to API Assignment</h1>
      <div style={styles.header}>
        <p style={styles.greeting}>Hello, {username}!</p>
        <button style={styles.logoutButton} onClick={handleLogout}>
          Logout
        </button>
        <p
          style={status === "Active" ? styles.activeStatus : styles.deactiveStatus}
          title={tooltip}
        >
          {status}
        </p>
      </div>
      <div style={styles.columns}>
        {/* First Column */}
        <div style={styles.column}>
          <h2>Devices</h2>
          {devices.length > 0 ? (
            devices.map((device, index) => (
              <div key={index} style={styles.deviceItem}>
                <p>Device ID: {device.device_id}</p>
                <p>Created By: {device.created_by}</p>
                <p>Created Time: {new Date(device.created_time).toLocaleString()}</p>
              </div>
            ))
          ) : (
            <p>{deviceMessage}</p>
          )}
        </div>

        {/* Second Column */}
        <div style={styles.column}>
          <h2>Actions</h2>
          <button style={styles.actionButton} onClick={() => setShowAddDevice(true)}>
            Add Device
          </button>
          <button style={styles.actionButton} onClick={() => setShowDeleteDevice(true)}>
            Delete Device
          </button>
          {showAddDevice && <AddDevice />}
          {showDeleteDevice && <DeleteDevice />}
        </div>

        {/* Third Column */}
        <div style={styles.column}>
          <h2>Device Count</h2>
          <div
            style={{
              width: "100%",
              maxWidth: "400px",
              height: "300px",
              margin: "0 auto",
            }}
          >
            <canvas id="deviceChart"></canvas>
          </div>
        </div>
      </div>
      
      {/* Footer */}
      <div style={styles.footer}>
        <p style={styles.footerItem}>SUGHNAN H R</p>
        <p style={styles.footerItem}>ALTEN GT</p>
        <p style={styles.footerItem}>HRSUGHNANKIRAN389@GMAIL.COM</p>
        <p style={styles.footerItem}>9480146265</p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#f0f4f8",
    padding: "20px",
    minHeight: "100vh",
  },
  header: {
    position: "absolute",
    top: "10px",
    right: "20px",
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  greeting: {
    fontSize: "1.2rem",
    color: "#555",
    fontFamily: "'Roboto', sans-serif",
  },
  logoutButton: {
    padding: "5px 10px",
    fontSize: "1rem",
    color: "#fff",
    backgroundColor: "#007bff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  activeStatus: {
    fontSize: "1rem",
    color: "green",
    fontWeight: "bold",
    cursor: "pointer",
  },
  deactiveStatus: {
    fontSize: "1rem",
    color: "red",
    fontWeight: "bold",
    cursor: "pointer",
  },
  heading: {
    fontSize: "2rem",
    color: "#333",
    fontFamily: "'Roboto', sans-serif",
    textAlign: "center",
    marginBottom: "30px",
  },
  columns: {
    display: "flex",
    justifyContent: "space-around",
    width: "100%",
  },
  column: {
    flex: 1,
    margin: "0 10px",
    textAlign: "center",
  },
  deviceItem: {
    fontSize: "1rem",
    marginBottom: "5px",
  },
  actionButton: {
    padding: "10px",
    margin: "10px",
    fontSize: "1rem",
    color: "#fff",
    backgroundColor: "#007bff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  footer: {
    position: "fixed",
    bottom: 0,
    width: "100%",
    backgroundColor: "#333",
    color: "#fff",
    textAlign: "center",
    padding: "10px 0",
    fontSize: "0.9rem",
    fontFamily: "'Roboto', sans-serif",
  },
  footerItem: {
    margin: "5px 0",
  },
};

export default Landing;
