import React, { useState } from 'react';
import axios from 'axios';

const GetAllDevices = () => {
  const [devices, setDevices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleGetAllDevices = async () => {
    setLoading(true);
    setError(null); // Reset error state on new fetch attempt
    try {
      const result = await axios.get('http://localhost:5000/api/getalldevices');
      console.log('Backend response:', result.data); // Debugging log
      setDevices(Array.isArray(result.data) ? result.data : []); // Ensure it's an array
    } catch (error) {
      console.error('Error fetching devices:', error); // Debugging log
      setError('Failed to fetch devices.');
      setDevices([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>All Devices</h2>
      <button onClick={handleGetAllDevices} disabled={loading}>
        {loading ? 'Fetching Devices...' : 'Fetch Devices'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {devices.length === 0 ? (
          <li>No devices available.</li>
        ) : (
          devices.map((device, index) => (
            <li key={index}>{device.name || device.device_name}</li>
          ))
        )}
      </ul>
    </div>
  );
};
export default GetAllDevices;
