import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart, PieController, ArcElement, Tooltip, Legend } from "chart.js";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBell } from "@fortawesome/free-solid-svg-icons";
import AddDevice from "./AddDevice"; // Import the AddDevice component
import DeleteDevice from "./DeleteDevice"; // Import the DeleteDevice component

Chart.register(PieController, ArcElement, Tooltip, Legend);

const Landing = () => {
  const [devices, setDevices] = useState([]);
  const [deviceMessage, setDeviceMessage] = useState("Fetching devices...");
  const [chartData, setChartData] = useState(null);
  const [chartInstance, setChartInstance] = useState(null);
  const [showAddDevice, setShowAddDevice] = useState(false);
  const [showDeleteDevice, setShowDeleteDevice] = useState(false);
  const [status, setStatus] = useState("Checking...");
  const [tooltip, setTooltip] = useState("");
  const [notificationMessage, setNotificationMessage] = useState(""); // State for notification message
  const navigate = useNavigate();
  const username = localStorage.getItem("username");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/");
  };

  // Fetch devices
  useEffect(() => {
    const fetchDevices = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/getalldevices");
        if (response.data && response.data.devices.length > 0) {
          setDevices(response.data.devices);
        } else {
          setDeviceMessage("No devices found.");
        }
      } catch (error) {
        setDeviceMessage("Error fetching devices.");
      }
    };
    fetchDevices();
  }, []);

  // Fetch device counts
  const fetchDeviceCounts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/devicescount");
      const data = response.data.counts;
      setChartData(data);

      if (chartInstance) {
        chartInstance.data.datasets[0].data = [
          data.countWithinOneHour,
          data.countBeforeOneHour,
        ];
        chartInstance.update();
      } else {
        initializeChart(data);
      }
    } catch (error) {
      console.error("Error fetching device counts:", error);
    }
  };

  // Initialize chart
  const initializeChart = (data) => {
    const ctx = document.getElementById("deviceChart").getContext("2d");
    const newChartInstance = new Chart(ctx, {
      type: "pie",
      data: {
        labels: ["Created within the last hour", "Created before one hour"],
        datasets: [
          {
            label: "Device Count",
            data: [data.countWithinOneHour, data.countBeforeOneHour],
            backgroundColor: ["#36A2EB", "#FF6384"],
            borderColor: ["#36A2EB", "#FF6384"],
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: { position: "top" },
        },
      },
    });
    setChartInstance(newChartInstance);
  };

  useEffect(() => {
    fetchDeviceCounts();
  }, []);

  // Fetch health status
  useEffect(() => {
    const checkHealth = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/healthcheck");
        if (response.data.statusCode === 200) {
          setStatus("Active");
          setTooltip("API is running and database connection is healthy");
        } else {
          setStatus("Deactive");
          setTooltip("API is down or database connection is unhealthy");
        }
      } catch (error) {
        setStatus("Deactive");
        setTooltip("Failed to connect to the API or database");
      }
    };
    checkHealth();
  }, []);

  // Handle notification click
  const handleNotificationClick = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/getnotification");
      if (response.data.statusCode === 200) {
        setNotificationMessage(`TOTAL NUMBER OF DEVICES ARE: ${response.data.deviceCount}`);
      } else {
        setNotificationMessage("Failed to fetch notification data.");
      }
    } catch (error) {
      setNotificationMessage("Error fetching notification.");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Hello, Welcome to API Assignment</h1>
      <div style={styles.header}>
        <p style={styles.greeting}>Hello, {username}!</p>
        <FontAwesomeIcon
          icon={faBell}
          style={styles.notificationIcon}
          title="Notifications"
          onClick={handleNotificationClick} // Add click handler
        />
        <button style={styles.logoutButton} onClick={handleLogout}>
          Logout
        </button>
        <p
          style={status === "Active" ? styles.activeStatus : styles.deactiveStatus}
          title={tooltip}
        >
          {status}
        </p>
      </div>
      {/* Display notification message */}
      {notificationMessage && <p style={styles.notificationMessage}>{notificationMessage}</p>}

      <div style={styles.columns}>
        {/* First Column */}
        <div style={styles.column}>
          <h2>Devices</h2>
          <div style={styles.deviceListContainer}>
            {devices.length > 0 ? (
              devices.map((device, index) => (
                <div key={index} style={styles.deviceItem}>
                  <p>Device ID: {device.device_id}</p>
                  <p>Created By: {device.created_by}</p>
                  <p>Created Time: {new Date(device.created_time).toLocaleString()}</p>
                </div>
              ))
            ) : (
              <p>{deviceMessage}</p>
            )}
          </div>
        </div>

        {/* Second Column */}
        <div style={styles.column}>
          <h2>Actions</h2>
          <button style={styles.actionButton} onClick={() => setShowAddDevice(true)}>
            Add Device
          </button>
          <button style={styles.actionButton} onClick={() => setShowDeleteDevice(true)}>
            Delete Device
          </button>
          {showAddDevice && <AddDevice />}
          {showDeleteDevice && <DeleteDevice />}
        </div>

        {/* Third Column */}
        <div style={styles.column}>
          <h2>Device Count</h2>
          <div
            style={{
              width: "100%",
              maxWidth: "400px",
              height: "300px",
              margin: "0 auto",
            }}
          >
            <canvas id="deviceChart"></canvas>
          </div>
        </div>
      </div>

      {/* Contact Details Row */}
      <div style={styles.contactDetails}>
        <p>SUGHNAN H R, ALTEN GT, HRSUGHNANKIRAN389@GMAIL.COM, 9480146265</p>
      </div>
    </div>
  );
};

const styles = {
  container: { /* Styles */ },
  // Define the styles here
  notificationMessage: {
    marginTop: "10px",
    color: "#007bff",
    fontWeight: "bold",
  },
};

export default Landing;
