import React, { useState } from "react";
import axios from "axios";
import "./AddDevice.css";

const AddDevice = () => {
  const [deviceName, setDeviceName] = useState("");
  const [deviceId, setDeviceId] = useState("");
  const [deviceDescription, setDeviceDescription] = useState("");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!deviceName.trim() || !deviceId.trim() || !deviceDescription.trim()) {
      setResponse("Please fill out all fields.");
      return;
    }

    setLoading(true);
    setResponse("");
    try {
      // Send POST request to add a device
      const result = await axios.post("http://localhost:5000/api/adddevice", {
        device_name: deviceName,
        device_id: deviceId,
        device_description: deviceDescription,
      });

      // Set response message
      setResponse(result.data.message || "Device added successfully!");
      setDeviceName("");
      setDeviceId("");
      setDeviceDescription("");

      // Reload the page after 2 seconds to reflect the new device
      setTimeout(() => window.location.reload(), 2000);
    } catch (error) {
      setResponse(error.response?.data?.message || "Failed to add device. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-device-container">
      <h2>Add Device</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter Device Name"
          value={deviceName}
          onChange={(e) => setDeviceName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Device ID"
          value={deviceId}
          onChange={(e) => setDeviceId(e.target.value)}
        />
        <textarea
          placeholder="Enter Device Description"
          value={deviceDescription}
          onChange={(e) => setDeviceDescription(e.target.value)}
        ></textarea>
        <button type="submit" disabled={loading}>
          {loading ? "Adding..." : "Add Device"}
        </button>
      </form>
      {response && <p className="response-message">{response}</p>}
    </div>
  );
};

export default AddDevice;
