import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Landing = () => {
  const [status, setStatus] = useState("Checking..."); // State for health status
  const username = localStorage.getItem("username"); // Retrieve username from localStorage
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove authentication token
    localStorage.removeItem("username"); // Remove username
    navigate("/"); // Redirect to login page
  };

  // Fetch health status
  useEffect(() => {
    const checkHealth = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/healthcheck");
        if (response.data.statusCode === 200) {
          setStatus("Active");
        } else {
          setStatus("Deactive");
        }
      } catch (error) {
        setStatus("Deactive");
      }
    };

    checkHealth();
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <p style={styles.greeting}>Hello, {username}!</p>
        <button style={styles.logoutButton} onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div style={styles.statusContainer}>
        <p style={status === "Active" ? styles.activeStatus : styles.deactiveStatus}>
          {status}
        </p>
      </div>
      <h1 style={styles.heading}>Hello, Welcome to API Assignment</h1>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#f0f4f8",
    padding: "20px",
  },
  header: {
    position: "absolute",
    top: "10px",
    right: "20px",
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  greeting: {
    fontSize: "1.2rem",
    color: "#555",
    fontFamily: "'Roboto', sans-serif",
  },
  logoutButton: {
    padding: "5px 10px",
    fontSize: "1rem",
    color: "#fff",
    backgroundColor: "#007bff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  statusContainer: {
    marginTop: "20px",
  },
  activeStatus: {
    fontSize: "1.2rem",
    color: "green",
    fontFamily: "'Roboto', sans-serif",
    fontWeight: "bold",
  },
  deactiveStatus: {
    fontSize: "1.2rem",
    color: "red",
    fontFamily: "'Roboto', sans-serif",
    fontWeight: "bold",
  },
  heading: {
    fontSize: "2rem",
    color: "#333",
    fontFamily: "'Roboto', sans-serif",
    textAlign: "center",
  },
};

export default Landing;
